(1, 1, 0, 'flexibility5.header', '', 0, 7, 0, '<!DOCTYPE html>\r\n<html class="no-js" lang="[[++cultureKey]]">\r\n    <head>\r\n        <base href="[[++site_url]]" />\r\n        <meta charset="utf-8">\r\n        <meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">\r\n        <title>[[*longtitle:notempty=`[[*longtitle]]`:default=`[[*pagetitle]]`]] | [[++site_name]]</title>\r\n        <meta name="description" content="[[*description]]">\r\n        <meta name="viewport" content="width=device-width, initial-scale=1.0"/>\r\n        <link rel="icon" type="image/png" href="[[++flex5.favicon]]">\r\n        <link rel="stylesheet" href="assets/components/flexibility5/css/foundation.css" />\r\n        <script src="assets/components/flexibility5/js/vendor/modernizr.js"></script>\r\n    </head>\r\n    <body>\r\n        [[!switch?\r\n            &get=`[[++flex5.menu_type]]`\r\n            &c1=`top-bar`\r\n            &do1=`[[$flex5.top-bar]]`\r\n            &c2=`off-canvas`\r\n            &do2=`[[$flex5.off-canvas]]`\r\n            &default=`[[$flex5.top-bar]]`\r\n        ]]', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/flex5.header.html'),
