(2, 1, 0, 'flexibility5.footer', '', 0, 7, 0, '	<footer>\r\n		<div class="row">\r\n	        <div class="large-12 columns">\r\n	        	[[++flex5.footer_content]]\r\n	        </div>\r\n	      </div>\r\n	</footer>\r\n	<script src="assets/components/flexibility5/js/vendor/jquery.js"></script>\r\n    <script src="assets/components/flexibility5/js/foundation.min.js"></script>\r\n    <script>\r\n    	$(document).foundation();\r\n    </script>\r\n  </body>\r\n</html>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/flex5.footer.html'),
