(4, 1, 0, 'flex5.off-canvas', '', 0, 7, 0, '<div class="off-canvas-wrap" data-offcanvas>\r\n    <div class="inner-wrap">\r\n        <header>\r\n            <nav class="tab-bar">\r\n                <section class="[[++flex5.top-bar-align]]-small">\r\n                <a class="[[++flex5.top-bar-align]]-off-canvas-toggle menu-icon" href="#"><span></span></a>\r\n                </section>\r\n\r\n                <section class="middle tab-bar-section">\r\n                <h1 class="title">[[++site_name]]</h1>\r\n                </section>\r\n            </nav>\r\n            <!-- Off Canvas Menu -->\r\n            <aside class="[[++flex5.top-bar-align]]-off-canvas-menu">\r\n                [[pdoMenu?\r\n                    &parents=`0`\r\n                    &level=`0`\r\n                    &tplOuter=`@INLINE <ul class="[[+classnames]] off-canvas-list">[[+wrapper]]</ul>`\r\n                    &tplInner=`@INLINE <ul class="[[+classnames]]">[[+wrapper]]</ul>`\r\n                    &tplParentRow=`@INLINE <li class="[[+classnames]]"><a href="[[+link]]" [[+attributes]]>[[+pagetitle]]</a>[[+wrapper]]</li>`\r\n                ]]\r\n            </aside>\r\n        </header>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/navigation/flex5.off-canvas.html'),
(5, 1, 0, 'flex5.orbit_slider', '', 0, 7, 0, '<div class="row">\r\n  <div class="large-12 columns">\r\n    <ul class="example-orbit" data-orbit data-options="\r\n    animation_speed: 1500,\r\n    animation: \'slide\',\r\n    navigation_arrows: true,\r\n    slide_number: true,\r\n    slide_number_text: \'of\',\r\n    bullets: true,\r\n    timer: true;\r\n    ">\r\n      [[!getImageList? \r\n        &tvname=`flex5.slider_items`\r\n        &tpl=`flex5.orbit_slider_tpl`\r\n      ]]\r\n    </ul>\r\n  </div>\r\n</div>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/slider/flex5.orbit_slider.html'),
(3, 1, 0, 'flex5.top-bar', '', 0, 7, 0, '<header>\r\n    <nav class="top-bar" data-topbar role="navigation">\r\n        <ul class="title-area">\r\n            <li class="name">\r\n                [[++flex5.top-bar-title:is=`yes`:then=`\r\n                    <h1><a href="[[~1]]">[[++site_name]]</a></h1>\r\n                `]]\r\n                \r\n            </li>\r\n            <!-- Remove the class "menu-icon" to get rid of menu icon. Take out "Menu" to just have icon alone -->\r\n            <li class="toggle-topbar menu-icon"><a href="#"><span>Menu</span></a></li>\r\n        </ul>\r\n        <section class="top-bar-section">\r\n            [[pdoMenu?\r\n                &parents=`0`\r\n                &level=`0`\r\n                &tplOuter=`@INLINE <ul class="[[+classnames]] [[++flex5.top-bar-align]]">[[+wrapper]]</ul>`\r\n                &tplInner=`@INLINE <ul class="[[+classnames]] dropdown">[[+wrapper]]</ul>`\r\n                &tplParentRow=`@INLINE <li class="[[+classnames]] has-dropdown"><a href="[[+link]]" [[+attributes]]>[[+pagetitle]]</a>[[+wrapper]]</li>`\r\n            ]]\r\n        </section>\r\n    </nav>\r\n</header>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/navigation/flex5.top-bar.html'),
(7, 1, 0, 'flex5.contact_form', '', 0, 7, 0, '[[!FormIt?\r\n  &hooks=`spam,email,redirect`\r\n  &emailTpl=`flex5.form_email_tpl`\r\n  &emailTo=`[[*flex5.form_to_email_adress]]`\r\n  &redirectTo=`[[*flex5.form_redirec_to_page]]`\r\n  &validate=`name:required,email:email:required,text:required:stripTags`\r\n]]\r\n<form action="[[~[[*id]]]]" method="post" class="form">\r\n  <input type="hidden" name="nospam:blank" value="[[+fi.nospam]]" />\r\n  <div class="row">\r\n    <div class="large-6 columns">\r\n      <label for="name">Name\r\n        <input type="text" placeholder="Name" [[!+fi.error.name:notempty=`class="error"`]] name="name" id="name" value="[[!+fi.name]]" />\r\n      </label>\r\n      [[!+fi.error.name:notempty=`[[!+fi.error.name]]`]]\r\n    </div>\r\n    <div class="large-6 columns">\r\n      <label>E-mail\r\n        <input type="email" name="email" placeholder="E-mail" [[!+fi.error.email:notempty=`class="error"`]] id="email" value="[[!+fi.email]]" />\r\n      </label>\r\n      [[!+fi.error.email:notempty=`[[!+fi.error.email]]`]]\r\n    </div>\r\n  </div>\r\n  <div class="row">\r\n    <div class="large-12 columns">\r\n      <label>Message\r\n        <textarea name="text" placeholder="Message" [[!+fi.error.text:notempty=`class="error"`]] id="text" cols="55" rows="7" value="[[!+fi.text]]">[[!+fi.text]]</textarea>\r\n      </label>\r\n      [[!+fi.error.text:notempty=`[[!+fi.error.text]]`]]\r\n    </div>\r\n  </div>\r\n  <div class="row">\r\n    <div class="large-12 columns">\r\n      <input type="submit" value="Send Contact Inquiry" class="button" />\r\n    </div>\r\n  </div>\r\n</form>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/contact/flex5.contact_form.html'),
(49, 1, 0, 'flex5.sections_inner_tpl', '', 0, 7, 0, '<div class="[[!switch? &get=`[[+total]]` &c1=`1` &do1=`small-12 large-12 column` &c2=`2` &do2=`small-12 medium-6 large-6 columns` &c3=`3` &do3=`small-12 large-4 columns` &c4=`4` &do4=`small-12 medium-6 large-3 columns` &default=`12`]]">\r\n	[[+column]]\r\n</div>', 0, 'a:0:{}', 1, 'assets/components/flexibility5/chunks/sections/flex5.sections_inner_tpl.html'),
(8, 0, 0, 'user.input.php', 'Chunk', 0, 15, 0, '<?php\n\n/**\n * Script to interact with user during [[+packageName]] package install\n *\n * Copyright [[+copyright]] by [[+author]] [[+email]]\n * Created on [[+createdon]]\n *\n[[+license]]\n *\n * @package [[+packageNameLower]]\n */\n\n/**\n * Description: Script to interact with user during [[+packageName]] package install\n * @package [[+packageNameLower]]\n * @subpackage build\n */\n\n/* The return value from this script should be an HTML form (minus the\n * <form> tags and submit button) in a single string.\n *\n * The form will be shown to the user during install\n *\n * This example presents an HTML form to the user with two input fields\n * (you can have as many as you like).\n *\n * The user\'s entries in the form\'s input field(s) will be available\n * in any php resolvers with $modx->getOption(\'field_name\', $options, \'default_value\').\n *\n * You can use the value(s) to set system settings, snippet properties,\n * chunk content, etc. based on the user\'s preferences.\n *\n * One common use is to use a checkbox and ask the\n * user if they would like to install a resource for your\n * component (usually used only on install, not upgrade).\n */\n\n/* This is an example. Modify it to meet your needs.\n * The user\'s input would be available in a resolver like this:\n *\n * $changeSiteName = (! empty($modx->getOption(\'change_sitename\', $options, \'\'));\n * $siteName = $modx->getOption(\'sitename\', $options, \'\').\n *\n * */\n\n$output = \'<p>&nbsp;</p>\n<p>Setting this option will do nothing because there is no resolver that acts on it.</p>\n<label for="sitename">The value here could be used to set the site_name system setting on install.</label>\n<p>&nbsp;</p>\n<input type="text" name="sitename" id="sitename" value="" align="left" size="40" maxlength="60" />\n<p>&nbsp;</p>\n<input type="checkbox" name="change_sitename" id="change_sitename" checked="checked" value="1" align="left" />&nbsp;&nbsp;\n<label for="change_sitename">Set site name on install</label>\n<p>&nbsp;</p>\';\n\n\nreturn $output;', 0, NULL, 0, ''),
(9, 0, 0, 'tvresolver.php', 'Chunk', 0, 15, 0, '<?php\n/**\n* Resolver to connect TVs to templates for [[+packageName]] extra\n*\n* Copyright [[+copyright]] by [[+author]] [[+email]]\n* Created on [[+createdon]]\n*\n[[+license]]\n* @package [[+packageNameLower]]\n* @subpackage build\n*/\n\n/* @var $object xPDOObject */\n/* @var $modx modX */\n/* @var $parentObj modResource */\n/* @var $templateObj modTemplate */\n\n/* @var array $options */\n\nif (!function_exists(\'checkFields\')) {\n    function checkFields($required, $objectFields) {\n        global $modx;\n        $fields = explode(\',\', $required);\n        foreach ($fields as $field) {\n            if (!isset($objectFields[$field])) {\n                $modx->log(modX::LOG_LEVEL_ERROR, \'[TV Resolver] Missing field: \' . $field);\n                return false;\n            }\n        }\n        return true;\n    }\n}\n\nif ($object->xpdo) {\n    $modx =& $object->xpdo;\n    switch ($options[xPDOTransport::PACKAGE_ACTION]) {\n        case xPDOTransport::ACTION_INSTALL:\n        case xPDOTransport::ACTION_UPGRADE:\n\n            $intersects = \'[[+intersects]]\';\n\n            if (is_array($intersects)) {\n                foreach ($intersects as $k => $fields) {\n                    /* make sure we have all fields */\n                    if (!checkFields(\'tmplvarid,templateid\', $fields)) {\n                        continue;\n                    }\n                    $tv = $modx->getObject(\'modTemplateVar\', array(\'name\' => $fields[\'tmplvarid\']));\n                    if ($fields[\'templateid\'] == \'default\') {\n                        $template = $modx->getObject(\'modTemplate\', $modx->getOption(\'default_template\'));\n                    } else {\n                        $template = $modx->getObject(\'modTemplate\', array(\'templatename\' => $fields[\'templateid\']));\n                    }\n                    if (!$tv || !$template) {\n                        $modx->log(xPDO::LOG_LEVEL_ERROR, \'Could not find Template and/or TV \' .\n                            $fields[\'templateid\'] . \' - \' . $fields[\'tmplvarid\']);\n                        continue;\n                    }\n                    $tvt = $modx->getObject(\'modTemplateVarTemplate\', array(\'templateid\' => $template->get(\'id\'), \'tmplvarid\' => $tv->get(\'id\')));\n                    if (! $tvt) {\n                        $tvt = $modx->newObject(\'modTemplateVarTemplate\');\n                    }\n                    if ($tvt) {\n                        $tvt->set(\'tmplvarid\', $tv->get(\'id\'));\n                        $tvt->set(\'templateid\', $template->get(\'id\'));\n                        if (isset($fields[\'rank\'])) {\n                            $tvt->set(\'rank\', $fields[\'rank\']);\n                        } else {\n                            $tvt->set(\'rank\', 0);\n                        }\n                        if (!$tvt->save()) {\n                            $modx->log(xPDO::LOG_LEVEL_ERROR, \'Unknown error creating templateVarTemplate for \' .\n                                $fields[\'templateid\'] . \' - \' . $fields[\'tmplvarid\']);\n                        }\n                    } else {\n                        $modx->log(xPDO::LOG_LEVEL_ERROR, \'Unknown error creating templateVarTemplate for \' .\n                            $fields[\'templateid\'] . \' - \' . $fields[\'tmplvarid\']);\n                    }\n\n\n                }\n\n            }\n            break;\n\n        case xPDOTransport::ACTION_UNINSTALL:\n            break;\n    }\n}\n\nreturn true;', 0, NULL, 0, ''),
(10, 0, 0, 'transportfile.php', 'Chunk', 0, 15, 0, '<?php\n/**\n * [[+elementType]] transport file for [[+packageName]] extra\n *\n * Copyright [[+copyright]] by [[+author]] [[+email]]\n * Created on [[+createdon]]\n *\n * @package [[+packageNameLower]]\n * @subpackage build\n */\n\nif (! function_exists(\'stripPhpTags\')) {\n    function stripPhpTags($filename) {\n        $o = file_get_contents($filename);\n        $o = str_replace(\'<\' . \'?\' . \'php\', \'\', $o);\n        $o = str_replace(\'?>\', \'\', $o);\n        $o = trim($o);\n        return $o;\n    }\n}\n/* @var $modx modX */\n/* @var $sources array */\n', 0, NULL, 0, ''),
(11, 0, 0, 'tutorial.html.tpl', 'Chunk', 0, 15, 0, '<p>[[+packageDescription]]</p>\n\n<p>(Jump to <a href="[[~[[*id]]]]#propertiesTable">Properties Table</a>.)</p>\n\n<h3>Installing [[+packageName]]</h3>\n\n<p>\nGo to System | Package Management on the main menu in the MODX Manager and click on the &quot;Download Extras&quot; button. That will take you to the\nRevolution Repository (AKA Web Transport Facility). Put [[+packageName]] in the search box and press Enter. Click on the &quot;Download&quot; button, and once the package is downloaded,\n click on the &quot;Back to Package Manager&quot; button. That should bring you back to your Package Management grid. Click on the\n&quot;Install&quot; button next to [[+packageName]] in the grid. The [[+packageName]] package should now be installed.</p>\n\n<h3>Usage</h3>\n\n\n\n\n<a name="propertiesTable"></a>\n\n<h3>[[+packageName]] Properties</h3>', 0, NULL, 0, ''),
(12, 0, 0, 'resourceresolver.php', 'Chunk', 0, 15, 0, '<?php\n/**\n* Resource resolver  for [[+packageName]] extra.\n* Sets template, parent, and (optionally) TV values\n*\n* Copyright [[+copyright]] by [[+author]] [[+email]]\n* Created on [[+createdon]]\n*\n[[+license]]\n* @package [[+packageNameLower]]\n* @subpackage build\n*/\n\n/* @var $object xPDOObject */\n/* @var $modx modX */\n/* @var $parentObj modResource */\n/* @var $templateObj modTemplate */\n\n/* @var array $options */\n\nif (!function_exists(\'checkFields\')) {\n    function checkFields($required, $objectFields) {\n        global $modx;\n        $fields = explode(\',\', $required);\n        foreach ($fields as $field) {\n            if (! isset($objectFields[$field])) {\n                $modx->log(modX::LOG_LEVEL_ERROR, \'[Resource Resolver] Missing field: \' . $field);\n                return false;\n            }\n        }\n        return true;\n    }\n}\nif($object->xpdo) {\n    $modx =& $object->xpdo;\n    switch ($options[xPDOTransport::PACKAGE_ACTION]) {\n        case xPDOTransport::ACTION_INSTALL:\n        case xPDOTransport::ACTION_UPGRADE:\n\n            $intersects = \'[[+intersects]]\';\n\n            if (is_array($intersects)) {\n                foreach ($intersects as $k => $fields) {\n                    /* make sure we have all fields */\n                    if (! checkFields(\'pagetitle,parent,template\', $fields)) {\n                        continue;\n                    }\n                    $resource = $modx->getObject(\'modResource\', array(\'pagetitle\' => $fields[\'pagetitle\']));\n                    if (! $resource) {\n                        continue;\n                    }\n                    if ($fields[\'template\'] == \'default\') {\n                        $resource->set(\'template\', $modx->getOption(\'default_template\'));\n                    } else {\n                        $templateObj = $modx->getObject(\'modTemplate\', array(\'templatename\' => $fields[\'template\']));\n                        if ($templateObj) {\n                            $resource->set(\'template\', $templateObj->get(\'id\'));\n                        } else {\n                            $modx->log(modX::LOG_LEVEL_ERROR, \'[Resource Resolver] Could not find template: \' . $fields[\'template\']);\n                        }\n                    }\n                    if (!empty($fields[\'parent\'])) {\n                        if ($fields[\'parent\'] != \'default\') {\n                            $parentObj = $modx->getObject(\'modResource\', array(\'pagetitle\' => $fields[\'parent\']));\n                            if ($parentObj) {\n                                $resource->set(\'parent\', $parentObj->get(\'id\'));\n                            } else {\n                                $modx->log(modX::LOG_LEVEL_ERROR, \'[Resource Resolver] Could not find parent: \' . $fields[\'parent\']);\n                            }\n                        }\n                    }\n\n                    if (isset($fields[\'tvValues\'])) {\n                        foreach($fields[\'tvValues\'] as $tvName => $value) {\n                            $resource->setTVValue($tvName, $value);\n                        }\n\n                    }\n                    $resource->save();\n                }\n\n            }\n            break;\n\n        case xPDOTransport::ACTION_UNINSTALL:\n            break;\n    }\n}\n\nreturn true;', 0, NULL, 0, ''),
