(1, 0, 0, 'StercSeoSiteMap', 'Outputs the sitemap based on the page settings made in SEO-tab', 0, 1, 0, 'require_once $modx->getOption(\'stercseo.core_path\',null,$modx->getOption(\'core_path\').\'components/stercseo/\').\'model/stercseo/stercseo.class.php\';\n$stercseo = new StercSeo($modx,$scriptProperties);\n$allowSymlinks = (isset($allowSymlinks)) ? $allowSymlinks : 0;\n$contexts = (isset($contexts)) ? explode(\',\',str_replace(\' \',\'\',$contexts)) : array($modx->resource->get(\'context_key\'));\nreturn $stercseo->sitemap($contexts, \'sitemap/rowTpl\', \'sitemap/outerTpl\', $allowSymlinks);', 0, NULL, '', 0, ''),
